package com.syntax.class01;

public class DataTypesAndVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//storing whole numbers in variables
		byte variable=127;
		short var1=32767;
		int var2=1000000; //most popular
		long var3=1000000000l;
		
		//storing decimels
		float myFloat=10.99f;
		double myDouble=100.99;
		
		//storing single character
		char singleLetter='N';
		char specialCharacter='%';
		
		//storing boolean values: yes or no
		boolean myBoo=true;
		boolean myBoo1=false;
		
		System.out.println(variable);
		System.out.println("variable");
		
		System.out.println(myBoo1);
		
		System.out.println();
				

	}

}
