package com.syntax.class04;


public class CastingInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * Widening/Implicit/Automatic Casting: (small->to big
		 */
		
		double bigBox=1288;
		System.out.println(bigBox);  //1288.0
		
		int r=10/3; //3
		double res=10/3;
		
		/*
		 * Narrowing/explicit/manualy casting : (big->small)
		 */
		
		int i=(int)10.89;
		System.out.println(i);
		
		//if statement: (boolean condition) {Code..}

		
		//if (boolean condition) { code...A}
		//else {if (boolean condition) code...B}
		
		//______________________________________________________
		
		//Nested If: if (boolean condition){ code...->if (boolean condition){code...
		//
	}
	

}
