package com.syntax.class07;

public class Task4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Print odd numbers from 100 to 1
		
				int i=100; 
				while(i>=1) {
					if(i%2!=0) {
						System.out.print(i+" ");
					}
					i--;
				}

				i=100;
				while(i>=1) {
					System.out.print(i+" ");
				}
				i-=1;
	}

}
